using System;class Player { static void Main(string[] args) { string[] k = Console.ReadLine().Split(' '); int a = int.Parse(k[0]); int b = int.Parse(k[3]); int c = int.Parse(k[4]); int d = int.Parse(k[7]); int[] e = new int[a]; for (int i = 0; i < d; i++) { k = Console.ReadLine().Split(' '); int f = int.Parse(k[0]); int g = int.Parse(k[1]); e[f] = g; } e[b] = c; while (true) { k = Console.ReadLine().Split(' '); int m = int.Parse(k[0]); int l = int.Parse(k[1]); string h = k[2]; if (m == -1) { Console.WriteLine("WAIT"); continue; } if (h == "RIGHT" && l > e[m] || h == "LEFT" && l < e[m]) { Console.WriteLine("BLOCK"); } else { Console.WriteLine("WAIT"); } } } }